package com.solucao.entity;

public class RequestParams {

	private int source;

	private int destiny;

	private int maxiNumberOfNodes;

	public RequestParams() {
		// TODO Auto-generated constructor stub
	}

	public int getSource() {
		return source;
	}

	public void setSource(int source) {
		this.source = source;
	}

	public int getDestiny() {
		return destiny;
	}

	public void setDestiny(int destiny) {
		this.destiny = destiny;
	}

	public int getMaxiNumberOfNodes() {
		return maxiNumberOfNodes;
	}

	public void setMaxiNumberOfNodes(int maxiNumberOfNodes) {
		this.maxiNumberOfNodes = maxiNumberOfNodes;
	}

}
